/* ==UserStyle==
@name         DeepL
@version      0.4.0
@namespace    userstyles.world/user/tidy-easy-stockade
@description  Dark theme for DeepL
@author       tidy-easy-stockade
@license      Copyleft
@preprocessor stylus

@var color baseColor "Base Theme Color" #916506
@var color accentColor "Accent Theme Color" #4902ea
==/UserStyle== */

:root {
    --accent: #4902ea;
    --accent-darken: #7b41fd;
    --accent-5: #4d02f8;
    --accent-10: #550afd;
    --accent-20: #6826fd;
    --accent-30: #7b41fd;
    --accent-40: #8d5cfe;
    --accent-50: #a077fe;
    --accent-60: #b392fe;
    --accent-70: #c6adfe;
    --accent-80: #d9c9ff;
    --accent-90: #ece4ff;
    --accent-100: #fff;
    --base-0: #916506;
    --base-5: #a27107;
    --base-10: #b37d07;
    --base-20: #d69509;
    --base-30: #f5ac0e;
    --base-40: #f6b830;
    --base-50: #f8c453;
    --base-60: #f9cf75;
    --base-70: #fbdb98;
    --base-80: #fce7ba;
    --base-90: #fef3dd;
    --base-100: #fff;
    --background: #916506;
    --text-color: var(--base-70);
  }
  body,
  .relative.bg-neutral-next-50 {
    background-color: var(--base-0) !important;
  }
  button {
    color: var(--text-color) !important;
  }
  button:hover {
    background-color: var(--base-20) !important;
  }
  .dl_body,
  .dl_body--text_page {
    color: var(--text-color);
  }
  .bg-white {
    background-color: var(--base-10);
  }
  .bg-mosque {
    background-color: var(--accent) !important;
  }
  [class^="BasePageHeader-module--container"] {
    background-color: var(--base-5);
  }
  [class^="BasePageHeader-module--container"] [class^="Logo-module--logoImg"],
  [class^="BasePageHeader-module--container"] [class*="LogoLink-module--translatorLink"] {
    filter: brightness(0) invert(1);
  }
  [class^="BasePageHeader-module--container"] [id^="headlessui-popover-button"] {
    background-color: var(--base-10);
  }
  [class^="BasePageHeader-module--container"] [id^="headlessui-popover-button"] a {
    color: var(--text-color);
  }
  [class^="BasePageHeader-module--container"] [id^="headlessui-popover-button"] a.bg-highlight,
  [class^="BasePageHeader-module--container"] [id^="headlessui-popover-button"] a:hover {
    background-color: var(--base-20);
  }
  [class^="BasePageHeader-module--container"] [id^="headlessui-popover-button"] + [id^="headlessui-popover-panel"] .text-neutral-700 {
    color: var(--text-color);
  }
  [class^="BasePageHeader-module--container"] [id^="headlessui-popover-button"] + [id^="headlessui-popover-panel"] .text-neutral-900 {
    color: var(--text-color);
  }
  [class^="BasePageHeader-module--container"] [id^="headlessui-popover-button"] + [id^="headlessui-popover-panel"] .text-dark-3 {
    color: var(--base-50);
  }
  [class^="BasePageHeader-module--container"] [id^="headlessui-popover-button"] + [id^="headlessui-popover-panel"] a:hover {
    background-color: var(--base-20);
  }
  [class^="BasePageHeader-module--container"] [class^="ProductNavigationLinks-module--container"] button {
    padding-right: calc(0.2em + 18px);
    padding-left: 0.2em;
  }
  [class^="BasePageHeader-module--container"] [class^="ProductNavigationLinks-module--container"] button::after {
    right: 0.2em;
    filter: brightness(0) invert(1);
  }
  [class^="BasePageHeader-module--container"] [class^="ProductNavigationLinks-module--container"] a {
    color: var(--text-color);
  }
  [class^="BasePageHeader-module--container"] [class^="ProductNavigationLinks-module--container"] a:hover {
    color: var(--base-80) !important;
    border-color: currentcolor;
  }
  [class^="BasePageHeader-module--container"] [class^="AppDownloadButton-module--appDownloadButton"] {
    background-color: var(--base-20);
  }
  [class^="BasePageHeader-module--container"] [class^="AppDownloadButton-module--appDownloadButton"]:hover {
