    color: var(--text-color);
    border-color: var(--base-20);
  }
  [class^="BasePageHeader-module--container"] [class^="ProductUpdatesContainer-module--updateList"] li {
    border-color: var(--base-30);
  }
  [class^="BasePageHeader-module--container"] [class^="ProductUpdatesContainer-module--updateList"] li,
  [class^="BasePageHeader-module--container"] [class^="ProductUpdatesContainer-module--updateList"] li p,
  [class^="BasePageHeader-module--container"] [class^="ProductUpdatesContainer-module--updateList"] li a {
    color: var(--text-color);
  }
  [class^="BasePageHeader-module--container"] [class^="ProductUpdateFooter-module--updateFooter"] {
    background-color: var(--base-10);
  }
  [class^="BasePageHeader-module--container"] [class^="ProductUpdateFooter-module--updateFooter"] a {
    color: var(--text-color);
  }
  [class^="BasePageHeader-module--container"] [class^="UserMenuNew-module--container"] button {
    background-color: var(--base-10);
    border-color: var(--base-30);
  }
  [class^="BasePageHeader-module--container"] [class^="UserMenuNew-module--container"] [class^="UserMenuFull-module--menuContent"] {
    background-color: var(--base-10);
  }
  #translation-modes-heading + div button > div {
    background-color: var(--base-10);
    border-color: var(--base-20);
  }
  #translation-modes-heading + div button > div svg {
    filter: brightness(0) invert(1);
  }
  #translation-modes-heading + div button > div > div div {
    color: var(--text-color) !important;
  }
  #translation-modes-heading + div button > div > div + div {
    background-color: var(--accent);
  }
  #translation-modes-heading + div a[href="/write"] div[class^="cardButton"] {
    background-color: var(--base-10);
    border-color: var(--base-20);
  }
  #translation-modes-heading + div a[href="/write"] div[class^="cardButton"] > div div {
    color: var(--text-color) !important;
  }
  #translation-modes-heading + div a[href="/write"] div[class^="cardButton"] > div + div {
    background-color: var(--accent);
  }
  [id^="headlessui-tabs-panel"] > div > div.border,
  [id^="headlessui-tabs-panel"] [class*="border"] {
    border-color: var(--base-20);
  }
  [id^="headlessui-tabs-panel"] section.rounded-inherit {
    border-color: var(--base-20);
  }
  [id^="headlessui-tabs-panel"] section.rounded-inherit [class*="TextTranslatorLayout-module--bothareas"] {
    border-color: var(--base-20);
  }
  [id^="headlessui-tabs-panel"] d-textarea {
    color: var(--text-color);
  }
  [id^="headlessui-tabs-panel"] d-textarea [data-placeholder] {
    color: var(--base-50);
  }
  [id^="headlessui-tabs-panel"] [id^="dictionary-header"],
  [id^="headlessui-tabs-panel"] [id^="dictionary-header"] + div {
    color: var(--text-color);
  }
  .lmt--web .lmt__sides_container {
    border-color: var(--base-20);
  }
  .integratedLanguageSelectors .lmt--web .lmt__sides_container .lmt__language_container {
    background-color: var(--base-10);
  }
  .lmt--web .lmt__language_select > .lmt__language_select__active strong,
  .lmt__formalitySwitch .lmt__formalitySwitch__toggler,
  .lmt__glossary_button .lmt__glossary_button_label,
  .lmt__language_container_switch {
    color: var(--text-color) !important;
  }
  #headlessui-listbox-button-8 {
    background-color: var(--base-10);
  }
  #headlessui-listbox-button-8 + ul[data-testid="write-language-selector_options"] {
    background-color: var(--base-10);
    color: var(--text-color);
    border: 1px solid var(--base-20);
  }
  #headlessui-listbox-button-8 + ul[data-testid="write-language-selector_options"] li {
    color: inherit;
  }
  #headlessui-listbox-button-8 + ul[data-testid="write-language-selector_options"] li[class*="active"] {
    background-color: var(--base-20);
  }
  #lmt__writeRevisionsUI_container > div {
    border-color: var(--base-20);
  }
  #lmt__writeRevisionsUI_container > div label {
    --background: var(--base-20);
    --backgroundHover: var(--base-30);
  }
  #lmt__writeRevisionsUI_container > div label span {
    color: var(--text-color);
  }
  #lmt__writeRevisionsUI_container > div label[class*="selected--"] {
    --background: var(--accent);
    --backgroundHover: var(--accent);
    color: var(--text-color);
  }
  .lmt__glossaryButton__desktop.switchedOn {
    border-color: var(--base-20);
  }
  .lmt__glossary_onOffSwitch_container {
    --background: var(--base-20);
    --backgroundHover: var(--base-30);
  }
  .lmt__glossary_onOffSwitch_container [class*="selected--"] {
    --background: var(--accent);
  }
  .lmt__glossary_onOffSwitch_container [class*="toggle--"]:hover {
    background-color: var(--base-20) !important;
  }
  .lmt--active_translation_request.lmt--active_translation_request--with_target_has_content .lmt__side_container--target .lmt__inner_textarea_container {
    box-shadow: inset 0 0 1px 14px var(--base-10);
