    border-bottom-color: var(--accent-60);
  }
  .lmt--web .lmt__textarea_container ul[aria-labelledby="alternatives-heading"] li {
    border-bottom-color: var(--base-30);
  }
  .lmt--web .lmt__textarea_placeholder_text {
    color: var(--base-40);
  }
  .relative.lmt__formalitySwitch > div {
    background-color: var(--base-10);
    border-color: var(--base-20);
  }
  .lmt--showing_alternatives .lmt__translations_as_text::before {
    border-color: var(--base-20);
  }
  .lmt__side_container .lmt__language_select__menu {
    background-color: var(--base-10);
    border-color: var(--base-20);
  }
  .lmt__side_container .lmt__language_select__menu input[type="text"] {
