    --button-border-color: var(--base-20);
  }
  [class*="dl_footerV2_container"] footer #languageSelectorIcon + div ul {
    background-color: var(--base-10) !important;
  }
  [class*="dl_footerV2_container"] footer #languageSelectorIcon + div ul li {
    --option-color: var(--text-color);
  }
  [class*="dl_footerV2_container"] footer #languageSelectorIcon + div ul li svg {
