@-moz-document domain('pages.github.com') {
	/*The_Pages*/
	body {
		background-color: var(--color-canvas-default);
		color: var(--color-fg-default);
	}
	#create-repo-step, #setup-in-desktop {
		background-color: var(--color-canvas-default);
	}
	.tutorial-list>li {
		background-color: var(--color-canvas-default);
		border-color: var(--color-border-default);
	}
	#video, #tutorial, .question .tabs a:not(.selected), .tutorial-list>li:last-child:before {
		background-color: var(--color-canvas-overlay);
	}
	.tabs a {
		background-color: var(--color-canvas-default);
		border-color: var(--color-border-default);
		color: var(--color-fg-default);
	}
	footer {
		border-color: var(--color-border-default);
		color: var(--color-fg-muted);
	}
	.hero-octicon {
		border-color: var(--color-border-default);
	}
	h1 {
		color: var(--color-fg-default);
	}
	footer .octicon-mark-github {
		color: var(--color-fg-subtle);
	}
	a, a:not(.tabs a):hover, .tabs a:not(.selected):hover {
		color: var(--color-accent-fg);
	}
	a:hover {
		opacity: .8;
	}
}
