/* ==UserStyle==
@name         Github: Twilight Syntax Highlighting
@namespace    USO Archive
@author       Miksago_
@description  Just a port of the twilight theme from http://pastie.org to GitHub. Enjoy, and feel free to port your own favour color schemes or suggest patchs, this was written based with php source code.
@version      20090519.13.01
@license      NONE
@preprocessor uso
==/UserStyle== */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("github.com") {

/* The Source Code area: */
  #files .file .data {
    background-color: #141414 !important;
    color:#F8F8F8 !important;
  }


/* Color Definitions: */

  /* Comments */
  .syntax .sd {
    color:#5F5A60 !important;
    font-style:italic !important;
  }

  /* Class names and stuff (class, public, function, etc) */
  .syntax .k {
    color:#F9EE98 !important;
    font-weight:normal !important;
  }

  /* Function Names */
  .syntax .nb {
    color:#DAD085 !important;
  }

  /* Class Names */
  .syntax .nc {
    color:#9B703F !important;
    font-weight:bold !important;
  }

  /* Variables */
  .syntax .nv {
    color:#7587A6 !important;
  }

  /* Class variables */
  .syntax .na {
    color:#7587A6 !important;
  }

  /* Operators ('->', '::', '>', '<', '=', etc ) */
  .syntax .o {
    color:#CDA869 !important;
  }

  /* Strings */
  .syntax .s1 {
    color:#8F9D6A !important;
  }

  /* Constants */
  .syntax .o + .k,
  .syntax .nx {
    color:#CF6A4C !important;
  }
}

