    opacity: .75;
    font-style: italic;
  }

  /* Any file row */
  [role=row] {
    /* ...which is MARKDOWN (has a link ending with ".md" or ".mdx" */
    &:has([href$=".md"], [href$=".mdx"]) {
      /* Override file icons */
      [role=gridcell]:first-child {
        & svg    { display: none; }
        &:before { content: "âœï¸" }
      }
    }
    /* ...IMAGE */
    &:has([href$=".png"],[href$=".jpg"],[href$=".jpeg"],[href$=".gif"]) {
      [role=gridcell]:first-child {
        & svg    { display: none;}
        &:before { content: "ðŸ–¼ï¸" }
      }
    }
    &:has([href$="CODEOWNERS"]) {
      [role=gridcell]:first-child {
        & svg    { display: none;}
        &:before { content: "ðŸ‘¥" }
      }
    }
    &:has([href$="LICENSE"]) {
      [role=gridcell]:first-child {
        & svg    { display: none;}
        &:before { content: "âš–ï¸" }
      }
    }
    &:has([href$="dockerfile"],[href$="Dockerfile"],[href$="docker-compose.yaml"]) {
      [role=gridcell]:first-child {
        & svg    { display: none;}
        &:before { content: "ðŸ“¦" }
