		--material-color-string: #C3E88D;
		--material-color-comment: #545454;
		--material-color-qualifier: #DECB6B;
	}

	.pl-k {
		color: var(--material-color-keyword) !important;
	}

	.pl-s {
		color: var(--material-color-string) !important;
	}

	.pl-c1 {
		color: var(--material-color-operator) !important;
	}

	.pl-smi {
		color: var(--material-color-variable-2) !important;
	}

	.pl-en {
		color: var(--material-color-def) !important;
	}

	.pl-s1 {
		color: var(--material-color-types) !important;
	}
}

.notification-indicator .mail-status {
	background-image: linear-gradient(var(--material-accent), var(--material-accent)) !important
}

code,
pre,
.blob-num,
.blob-code-inner {
	font-family: "Targa MS" !important;
}

.branch-name,
.commit .sha-block,
.commit .sha {
	font-family: "MesloLGS NF" !important font-size: 13px;
}
}

