@name         Furdox's Conflict Discord Theme 1
@namespace    USO Archive
@author       Furdox(.tk)
@description  Combines 3 themes together. Compacted by Furdox(.tk). Not garanteed to work.
@version      20230306.21.27
@license      NONE
@preprocessor uso
==/UserStyle== */
@-moz-document domain(discord.com) {


/* ------BACKGROUND------ */
  body
  {
    background: url(https://i.imgur.com/iqFVASf.gif);
    background-size: cover;
  }

  .appMount-3lHmkl
  {
    background: rgba(225, 225, 225, .6);
  }

/* ------REMOVE GREY/WHITE BG COLORS------ */
  .theme-dark,
.theme-light
  {
    --header-primary: #fff;
    --header-secondary: #fff;
    --text-normal: #fff;
    --text-muted: #fff;
    --channels-default: #fff;
    --interactive-normal: #fff;
    --interactive-hover: #fff;
    --interactive-active: #fff;
    --interactive-muted: #fff;
    --background-primary: transparent;
    --background-secondary: transparent;
    --background-secondary-alt: transparent;
    --background-tertiary: transparent;
    --background-floating: rgba(0, 0, 0, .8);
    --channeltextarea-background: transparent;
    --deprecated-panel-background: transparent;
    --background-modifier-hover: rgba(0, 0, 0, .42);
    --background-modifier-selected: rgba(0, 0, 0, .45);
  }

/* ------POP-UP------ */
  .modal-yWgWj-,
