@name         Discord Dark Maroon
@namespace    USO Archive
@author       phit
@description  A dark theme for Discord based on Slack's aubergine/maroon style.  Tested with Chrome and Firefox Nightly.
@version      20230814.19.17
@license      CC0-1.0
@preprocessor uso
==/UserStyle== */
@-moz-document domain("discord.com") {

.theme-dark {
    --header-primary: #fff;
    --header-secondary: #b9bbbe;
    --text-normal: #dcddde;
    --text-muted: hsla(0,0%,100%,.6);
    --text-link: #00b0f4;
    --channels-default: rgba(255,255,255,0.7);
    --interactive-normal: #b9bbbe;
    --interactive-hover: #dcddde;
    --interactive-active: #fff;
    --interactive-muted: hsla(0,0%,100%,.4);
    --background-primary: #3e313c;
    --background-secondary: #4d394b;
    --background-secondary-alt: #342932;
    --background-tertiary: #352c34;
    --background-accent: #ffffff3d;
    --background-floating: #3e313c;
    --background-message-hover: #332831;
    --background-modifier-hover: #443442;
    --background-modifier-active: rgba(62,49,60,1);
    --background-modifier-selected: rgba(62,49,60,0.9);
    --background-modifier-accent: hsla(0,0%,100%,0.06);
    --elevation-low: 0 1px 0 rgba(4,4,5,0.2),0 1.5px 0 rgba(6,6,7,0.05),0 2px 0 rgba(4,4,5,0.05);
    --elevation-high: 0 8px 16px rgba(0,0,0,0.24);
    --guild-header-text-shadow: 0 1px 1px rgba(0,0,0,0.4);
    --channeltextarea-background: rgba(0, 0, 0, 0.17);
    --activity-card-background: #202225;
