sourceCode:
/* ==UserStyle==
@name         Github Dark Wide Transparent
@namespace    USO Archive
@author       doublehelix
@description  A wide GitHub theme with transparent panes revealing a background wallpaper image.    If you like this style, please help others find it by giving it the thumbs up in UserStyles.org.    *** This is currently a work-in-progress (re-write) - Hopefully I'll be making daily updates to cover anything I've missed so be patient while I find them all. :-)
@version      20200209.23.14
@license      CC-BY-4.0
@preprocessor uso
==/UserStyle== */
@-moz-document domain("raw.githubusercontent.com"), domain("render.githubusercontent.com"), regexp("^https?://((gist|guides|help|raw|status|developer).)?github.com((?!generated_pages/preview).)*$") {
/*! Github Dark Wide Transparent Theme v2.3.0 (10-Feb-2020) */
/*
   * https://github.com/doublehelix/Stylish-Themes
   * https://userstyles.org/styles/126131
   * License: http://creativecommons.org/licenses/by/4.0/
   */
#com #not-footer #content-wrapper {
  box-sizing: border-box;
  min-width: 940px;
  width: 80%;
  margin: 0 auto;
}
div.content-header h6 {
  color: #fff;
}
div.content-header h6 a {
  color: #6e6;
}
.site {
  box-sizing: border-box;
  min-width: 940px;
  width: 100%;
  margin: 0 auto;
}
.site div.content-header {
  border-bottom: none;
}
div.article {
  box-sizing: border-box;
  min-width: 690px;
  width: 73%;
  padding: 1em;
  background: rgba(34, 40, 48, 0.75);
  border: 1px solid #ccc;
}
div.article h1,
div.article h1 span,
div.article h2,
div.article h2 span {
  color: #fff;
}
div.article #article-platform-nav ul li.selected a {
  color: #afa;
}
.sidebar {
  box-sizing: border-box;
  min-width: 219px;
  width: 26%;
  border-left: none;
  padding: 5px 0 15px 15px;
}
.sidebar h3 {
  color: #fff;
}
.markdown-format .intro {
  color: #ccc;
}
.markdown-format code,
.markdown-format pre {
  color: #ccc;
  background: rgba(17, 34, 51, 0.75);
}
.markdown-format .alert a,
.markdown-format .tip a,
.markdown-format .alert a:hover,
.markdown-format .tip a:hover,
.markdown-format .alert a:active,
.markdown-format .tip a:active,
.markdown-format .alert a:visited,
.markdown-format .tip a:visited {
  color: #4183c4;
}
body,
.release-authorship,
.gh-header-meta {
  color: #ccc;
}
.Header {
  border-bottom: 1px solid #ccc;
}
p {
  text-shadow: 0 0 2px rgba(0, 0, 0, 0.3);
}
.signup-prompt-bg {
  background-image: linear-gradient(180deg, rgba(34, 40, 48, 0.3) 50%, rgba(58, 60, 68, 0.4)), linear-gradient(70deg, rgba(0, 0, 0, 0.2) 32%, rgba(34, 48, 64, 0.4));
}
.signup-prompt-bg a.btn.btn-blue {
  color: #fff;
}
.session-authentication .create-account-callout {
  background: rgba(34, 40, 48, 0.75);
}
.setup-wrapper,
.setup-info-module,
.session-authentication .two-factor-help,
.auth-form-body {
  background: rgba(34, 40, 48, 0.75);
  border: 1px solid #789;
}
.session-authentication .two-factor-help {
  padding: 1em;
}
.setup-wrapper {
  border-radius: 4px;
  padding: 1em;
  width: 70%;
  min-width: 750px;
}
.setup-wrapper .lead {
  color: #ccc;
}
.container,
.container-lg,
.container-xl,
.container-lg.p-responsive,
.container-xl.p-responsive {
  width: 80%;
  min-width: 980px;
  max-width: unset;
}
.container-lg.p-responsive,
.container-xl.p-responsive {
  border-radius: 4px 0;
  padding: 0;
}
.Header .container-lg.p-responsive,
.Header .container-xl.p-responsive {
  background: none;
  border: none;
  margin-bottom: 0;
  padding: 0;
}
.MarketingBody > p {
  color: #ccc;
}
.MarketingBody blockquote {
  color: #fff;
}
.MarketingBody ul,
.MarketingBody ol {
  color: #fff;
}
.gh-header,
.wiki-body {
  padding: 1em;
}
.gh-header .gh-header-sticky.is-stuck + .gh-header-shadow {
  background-color: rgba(34, 40, 48, 0.75);
}
.gh-header,
.wiki-body,
.wiki-wrapper .wiki-auxiliary-content {
  color: #fff;
  background: rgba(34, 40, 48, 0.75);
  margin-bottom: 1em;
  border: 1px solid #ccc;
}
.repohead h1 .octicon {
  color: #fff;
}
.repohead h1.private .octicon {
  color: #ff0;
}
.Label--outline {
  color: #ccc;
}
.private .Label--outline {
  color: #fb0;
  border-color: #fb0;
  background: #c00;
}
/**************************************
 * MENUS
 **************************************/
.underline-nav-item {
  color: #ccc;
}
.underline-nav-item:hover,
.underline-nav-item:focus {
  color: #fff;
}
.underline-nav-item.selected {
  color: #fff;
  border-bottom-color: #6e6;
  border-bottom-width: 4px;
}
.pagehead-tabs-item,
.reponav-item,
a.reponav-item,
.reponav-item .octicon {
  color: #fff !important;
}
.pagehead-tabs-item:hover,
.reponav-item:hover,
a.reponav-item:hover,
.reponav-item .octicon:hover,
.pagehead-tabs-item:focus,
.reponav-item:focus,
a.reponav-item:focus,
.reponav-item .octicon:focus,
.pagehead-tabs-item:hover svg,
.reponav-item:hover svg,
a.reponav-item:hover svg,
.reponav-item .octicon:hover svg,
.pagehead-tabs-item:focus svg,
.reponav-item:focus svg,
a.reponav-item:focus svg,
.reponav-item .octicon:focus svg {
  color: #6e6 !important;
}
.pagehead-tabs-item.selected,
.reponav-item.selected,
a.reponav-item.selected,
.reponav-item .octicon.selected {
  color: #fff;
  border-top-color: #6e6;
  border-top-width: 4px;
  background: rgba(17, 34, 51, 0.75);
}
.tabnav-tabs {
  margin-bottom: -1px;
  padding: 0 20px 0 0;
  border-radius: 4px 4px 0 0;
  border-bottom: 1px solid #e1e4e8;
}
.tabnav-extra {
  margin-right: 0;
  background: rgba(34, 40, 48, 0.75);
  height: 40px;
  border: 1px solid #ccc;
  padding: 10px 10px 10px 100px;
}
.tabnav-tab {
  color: #fff !important;
  background-color: rgba(32, 64, 96, 0.3);
  border-color: #a1a5aa;
  border-radius: 3px 3px 0 0;
}
.tabnav-tab:hover,
.tabnav-tab.selected,
.tabnav-tab[aria-selected="true"] {
  background-color: rgba(64, 96, 164, 0.75);
  color: #6e6 !important;
}
.dropdown-menu {
  background-color: #222830;
  border: 1px solid rgba(255, 255, 255, 0.5);
}
.dropdown-item {
  color: inherit;
}
.Counter {
  background-color: #fff;
  color: #333;
}
.discussion-sidebar-heading {
  color: #fff;
}
.discussion-sidebar-toggle:hover,
.discussion-sidebar-toggle:focus {
  color: #afa;
}
.protip {
  background: rgba(17, 34, 51, 0.75);
  padding: 1em;
  border: 1px solid #6e6;
  margin: 0;
}
.protip strong {
  color: #fff;
}
.protip .octicon {
  color: #fff !important;
}
table.files td.icon,
table.files td.icon .octicon-file-directory {
  color: #fff;
}
.mini-repo-list .private {
  border-right: 4px solid #f00;
}
.public .mini-repo-list-item .repo-icon,
.private .mini-repo-list-item .repo-icon,
.mini-repo-list-item .stars {
