@license      CC-BY-SA-4.0
@preprocessor uso
==/UserStyle== */
@-moz-document url-prefix("https://github.com/") {
  .Header {
    background: linear-gradient(to right, #22276c, #586aa5, #a9c7f6);
  }
  .Header .header-search-wrapper {
    border-radius: 0;
    border-top: inset 2px #999;
    border-left: inset 2px #999;
    border-bottom: inset 2px #ddd;
