    background-color: #fff;
    color: black !important;
  }
  .pagehead {
    background-color: #d4d2cd !important;
    border-bottom: none;
  }
  .social-count {
    background-color: #d4d2cd !important;
    border-radius: 0;
  }
  .reponav .selected {
    border-top: 2px solid #ddd;
